SELECT count(*) AS count_1  FROM (SELECT (SELECT thread.updated_at  FROM thread  WHERE thread.id = project_thread.thread_id) AS anon_2, project_thread.project_id AS project_thread_project_id, project_thread.thread_id AS project_thread_thread_id, project_thread.message AS project_thread_message, project_thread.created_at AS project_thread_created_at, project_thread.created_by AS project_thread_created_by  FROM project_thread  WHERE %s = project_thread.project_id ORDER BY anon_2 DESC) AS anon_1
